function addEventFile(e){"checkbox"===e.type?e.addEventListener("click",function(e){showHiddenCheckBox(e.target)}):e.addEventListener("change",function(e){showHiddenSelect(e.target)})}function showHiddenCheckBox(e){let t=document.querySelectorAll(`.j__${e.name}`);for(let n=0;n<t.length;n++)e.checked?t[n].parentNode.parentNode.classList.remove("hidden"):t[n].parentNode.parentNode.classList.add("hidden")}function showHiddenSelect(e){let t="j__"+e.value,n=document.querySelectorAll(`.${e.name}`);for(let e=0;e<n.length;e++)n[e].classList.contains(t)?n[e].parentNode.parentNode.classList.remove("hidden"):n[e].parentNode.parentNode.classList.add("hidden")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".form-row select");for(let t=0;t<e.length;t++)addEventFile(e[t]),showHiddenSelect(e[t]);const t=document.querySelectorAll(".form-row input[type=checkbox]");for(let e=0;e<t.length;e++)addEventFile(t[e]),showHiddenCheckBox(t[e])});