# 
# 
# 
# 
# 
# 

import argparse
import collections
import itertools
import shutil
import sys

import colorama


def chunker(reader, width):
    "Yield chunks from lines in reader with given width."
    for line in reader:
        line = line[:-1]  # Chop off newline character.

        if line == '':
            yield ''
            continue

        for offset in range(0, len(line), width):
            yield line[offset:(offset + width)]


def main():
    "Main entry-point for following streams."
    parser = argparse.ArgumentParser()
    parser.add_argument('--limit', type=int, default=10)
    parser.add_argument('--width', type=int, default=None)
    args = parser.parse_args()

    limit = args.limit
    width = args.width

    if width is None:
        width, _ = shutil.get_terminal_size()

    chunks = chunker(sys.stdin, width)
    lines = collections.deque(itertools.islice(chunks, limit), limit)

    colorama.init()

    for line in lines:
        print(line)

    for chunk in chunks:
        lines.append(chunk)
        move_cursor_up_lines = '\033[{}A'.format(limit)
        print(move_cursor_up_lines, end='', flush=True)
        for line in lines:
            clear_line = '\033[2K'
            print(clear_line, end='', flush=True)
            print(line)


if __name__ == '__main__':
    main()
