def addition(a, b):
    """
    Adds two given values

    Parameters: 
    a (int): First value
    b (int): Second value

    Returns:
    int: sum result
    """
    return a + b


def substraction(a, b):
    """
    Substract one operator from another one

    Parameters:
    a (int): First value
    b (int): Second value

    Returns:
    int: subtraction result
    """
    return a - b


def multiplication(a, b):
    """
    Multiplies one operator to another one

    Parameters:
    a (int): First value
    b (int): Second value

    Returns:
    int: multiplication result
    """
    return a * b


def division(a, b):
    """
    Divides one operator from another one

    Parameters:
    a (int): First value
    b (int): Second value

    Returns:
    int: division result
    """
    return a / b


def modulus(a, b):
    """
    Divides one operator from another one

    Parameters:
    a (int): First value
    b (int): Second value

    Returns:
    int: division remainder
    """
    return a % b
